import React, { useState, useEffect } from 'react'
import { useTrans } from '../../../context/TransactionContext'



const dur = ['monthly', 'quarterly', 'yearly'];

const baseBanks = { SBI: 0, AXIS: 0, IDBI: 0, MISC: 0 };

const monthTrack = {
  '01': ['Jan', { ...baseBanks }],
  '02': ['Feb', { ...baseBanks }],
  '03': ['Mar', { ...baseBanks }],
  '04': ['Apr', { ...baseBanks }],
  '05': ['May', { ...baseBanks }],
  '06': ['Jun', { ...baseBanks }],
  '07': ['Jul', { ...baseBanks }],
  '08': ['Aug', { ...baseBanks }],
  '09': ['Sep', { ...baseBanks }],
  '10': ['Oct', { ...baseBanks }],
  '11': ['Nov', { ...baseBanks }],
  '12': ['Dec', { ...baseBanks }]
};


function Showsave({ data }) {

  const [duration, setDuration] = useState('monthly');
  const [yearSelector, setYearSelector] = useState([]);
const { trans } = useTrans();



const dataProcessing = () => {
  const yearMap = {};
  
  data.forEach(entry => {
    const year = entry.createdAt.slice(0, 4);
    const month = entry.createdAt.slice(5, 7);
    const bank = entry.category;
    const amount = Number(entry.deposit);

    if (!yearMap[year]) {
      // Clone a fresh monthTrack with fresh bank objects for immutability
      yearMap[year] = {};
      Object.keys(monthTrack).forEach(m => {
        yearMap[year][m] = [monthTrack[m][0], { ...baseBanks }];
      });
    }

    if (yearMap[year][month]) {
      if (yearMap[year][month][1][bank] !== undefined) {
        yearMap[year][month][1][bank] += amount;
      }
    }
  });

  // Convert yearMap to array if needed
  setYearSelector(Object.entries(yearMap));
};


  useEffect(() => {
    dataProcessing()
    console.log("this is insight ",yearSelector);
     

  }, [trans])

  useEffect(() => {
         console.log("this is insight ",yearSelector);


  }, [yearSelector])


  return (
    <>
      <pre>{JSON.stringify(yearSelector, null, 2)}</pre>

    </>

  )
}

export default Showsave
