import React, { useState } from 'react'

function GoldInsight({ data }) {
    const [selector, setSelector] = useState({ Qty: 0, Amt: 0, Inv: 0, Data: [] })
    const [curr, setCurr] = useState();

    const findInv=()=>{

        let totalInv = 0;
        let totalMaketSellPrice = selector.Qty*curr;
        totalInv = totalMarketSellPrice - selector.Amt;
  
        return totalInv;
    }

    const dataProcessing = () => {
        const dataGold = data?.dataGold || [];
        let qtyTotal = 0
        let amtTotal = 0
        let invTotal = 0
        dataGold.map((val, ind) => {

            qtyTotal += Number(val.qtyPurchased || 0);
            amtTotal += Number(val.purchaseAmount || 0);


        })
        let totalMaketSellPrice = selector.Qty*curr;
        let totalInv = totalMarketSellPrice - selector.Amt;
        invTotal = totalInv || 0
        setSelector((prev) => ({ ...prev, Qty: qtyTotal, Amt: amtTotal, Inv: invTotal, Date: dataGold }))
    }


    return (
        <>
            <p>Net Qty: </p>
            <p>Net Amt: </p>
            <p>Net Profit/Loss: </p>
            <input placeholder='Enter Current Sell Pricce Of Gold' name='curr' value={curr} onChange={(e) => setCurr(e.target.value)} />
        </>
    )
}

export default GoldInsight
