    const category = TakeInput('SBI')
    const deposit = TakeInput(0)
    const date = TakeInput(new Date().toISOString().substring(0, 16))
    const cmt = TakeInput('')
    const debit = TakeInput(0)

    const handleEntry=async()=>{


    }
  
    return (
    <>
         <select name='category' { ...category.bind}>
            {
                catSave.map((val, ind)=>{
                    return (
                        <>
                        <option value={val} key={ind}>{val}</option>
                        </>
                    )
                })
            }
         </select>
         <input type='number' { ...deposit.bind} placeholder='Enter Amount To Be Deposited' />
         <input type='date' { ...date.bind} placeholder='Select Date' />
         <input type='text' { ...cmt.bind} placeholder='Enter Comments' />
         <input type='number' { ...debit.bind} placeholder='Amount Debited From' />
         <button onClick={handleEntry}>Enter</button> 
    </>
  )
}

export default SaveEntry
