import React, { useEffect, useState } from 'react'
import '../details/Showsave.css'


const months = {
    '1': 'Jan',
    '2': 'Feb',
    '3': 'Mar',
    '4': 'Apr',
    '5': 'May',
    '6': 'Jun',
    '7': 'Jul',
    '8': 'Aug',
    '9': 'Sep',
    '10': 'Oct',
    '11': 'Nov',
    '12': 'Dec',
};


const quaterly = {
    1: ['1', 'Jan'],
    2: ['4', 'Apr'],
    3: ['7', 'July'],
    4: ['10', 'Oct']
}




function Showsave({ data }) {

    const [duration, setDuration] = useState('monthly');
    const [categories, setCategories] = useState([])
    const [records, setRecords] = useState([]

    )
    const durationSelector = () => {

        return (
            <>
                <select value={duration} onChange={(e) => setDuration(e.target.value)}>
                    <option value='monthly'>Monthly</option>
                    <option value='quaterly'>Quantery</option>
                    <option value='yearly'>Yearly</option>
                </select>
            </>
        )
    }

    const handleColSpan = () => records.length || 1;


    const getUniqueCategories = () => {
        const cats = [];
        for (let i = 0; i < data.length; i++) {
            const cat = data[i].category;
            if (!cats.includes(cat)) {
                cats.push(cat);
            }
        }
        setCategories(cats);
    };

    const getRecords = () => {
        const recs = [];
        let month = new Date().getMonth() + 1; // 1-12
        let year = new Date().getFullYear();

        for (let i = 0; i < 36; i++) {
            const mn = months[month.toString()];
            const shortYear = year.toString().slice(2, 4);
            recs.push(`${mn} ${shortYear}`);

            switch (duration) {
                case 'monthly':
                    month -= 1;
                    break;
                case 'quaterly':
                    month -= 3;
                    break;
                case 'yearly':
                    month -= 12;
                    break;
                default:
                    break;
            }

            while (month <= 0) {
                month += 12;
                year--;
            }
        }

        setRecords(recs.reverse());
    };



    useEffect(() => {
        if (data && data.length > 0) {
            getUniqueCategories();
            getRecords();
        }
    }, [data, duration]);


    useEffect(() => {

        console.log('this is cats', categories)
    }, [categories])

    return (
        <>
            <h4>Saving Insights</h4>
            <div style={{ overflowX: 'auto', maxWidth: '100%' }}>
                <table>
                    <thead>
                        <tr>
                            <th>Account</th>
                            <th colSpan={records.length}>{durationSelector()}</th>
                        </tr>
                        <tr>
                            <td></td>
                            {records.map((val, key) => (
                                <td key={key}>{val}</td>
                            ))}
                        </tr>
                    </thead>
                    <tbody>
                        {categories.map((val, key) => (
                            <tr key={key}>
                                <td>{val}</td>
                                {records.map((_, i) => (
                                    <td key={i}></td> // Placeholder for your actual data
                                ))}
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </>
    )

}

export default Showsave
