import React from 'react';
import Login from './Login';
import Register from './Register';
import { useFormContext } from '../context/FormContext';
import '../user/Form.css';

function Form() {
  const { view, setView } = useFormContext();

  const renderForm = () => {
    switch (view) {
      case 0:
        return <Login />;
      case 1:
        return <Register />;
      default:
        return null;
    }
  };

  return (
    <div className="form-wrapper">
      <div className="form-card">

        <div className="form-content">{renderForm()}</div>

        <div className="toggle-buttons">
          <button
            className={view === 0 ? 'active' : ''}
            onClick={() => setView(0)}
          >
            Login
          </button>
          <button
            className={view === 1 ? 'active' : ''}
            onClick={() => setView(1)}
          >
            Register
          </button>
        </div>

      </div>
    </div>
  );
}

export default Form;
