import React from 'react'
import Login from './Login'
import Register from './Register'
import Inputuser from './Inputuser'

function Form() {

    


    const handleToggle= async(numb)=>{
         switch (numb) {
            case 0: return <Login />
            case 1: return <Register />
         
            default:
                break;
         }
    }


  return (
    <>
    <div className='formDiv'>
        <div className='formChoiceButton'>
            <label>
                <button onClick={()=>{handleToggle(0)}}>Login</button>
                <button onClick={()=>{handleToggle(1)}}>Register</button>
            </label>
        </div>
    </div>
    </>
  )
}

export default Form
