import React from 'react';
import { PieChart, Pie, Cell, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const COLORS = ['#FFD700', '#6A5ACD', '#FF6347', '#20B2AA', '#8B0000', '#6495ED'];

function Invest({ data }) {
  if (!data) return null;

  const chartData = [
    { name: 'Gold (₹)', value: data.gold || 0 },
    { name: 'Mutual Fund', value: data.mutualfund || 0 },
    { name: 'Fixed Rate Bond', value: data.fixedratebond || 0 },
    { name: 'Fixed Deposit', value: data.fixeddeposit || 0 },
    { name: 'Gold Bond', value: data.goldbond || 0 },
    { name: 'Real Estate', value: data.realestate || 0 },
  ].filter(item => item.value > 0);

  return (
    <div style={{ display: 'flex', justifyContent: 'space-between', gap: '2rem', flexWrap: 'wrap' }}>
      
      {/* LEFT: Table */}
      <div className='left' style={{ flex: '1' }}>
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th style={{ borderBottom: '1px solid #ccc', textAlign: 'left', padding: '8px' }}>Investment Type</th>
              <th style={{ borderBottom: '1px solid #ccc', textAlign: 'right', padding: '8px' }}>Amount (₹)</th>
            </tr>
          </thead>
          <tbody>
            {chartData.map((entry, index) => (
              <tr key={index}>
                <td style={{ padding: '8px' }}>{entry.name}</td>
                <td style={{ padding: '8px', textAlign: 'right' }}>{entry.value}</td>
              </tr>
            ))}\
          </tbody>
        </table>
      </div>

      {/* RIGHT: Pie Chart */}
      <div className='right' style={{ flex: '1', minWidth: '300px', height: 300 }}>
        <ResponsiveContainer>
          <PieChart>
            <Pie
              data={chartData}
              dataKey="value"
              nameKey="name"
              cx="50%"
              cy="50%"
              outerRadius={100}
              innerRadius={50}
              fill="#8884d8"
              label
            >
              {chartData.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            <Tooltip />
            <Legend />
          </PieChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
}

export default Invest;
