import React from 'react';
import './TransactionCard.css';

function TransactionCard({ data, onDelete, onEdit }) {
  const { type, amt, desc, cmt, _id, date } = data;

  return (
      <div className="transaction-card">
        <div className="transaction-header">
          <h3 className="transaction-type">{desc}</h3>
          <span className="transaction-date">
            {new Date(date).toLocaleDateString()} • {new Date(date).toLocaleTimeString()}
          </span>
        </div>

        <div className="transaction-body">
          <p><strong>Type:</strong> ₹{amt}</p>
          <p><strong>Description:</strong> {desc}</p>
          <p><strong>Comment:</strong> {cmt || "—"}</p>
        </div>

        <div className="transaction-actions">
          <button className="btn edit-btn" onClick={() => onEdit(_id)}>Edit</button>
          <button className="btn delete-btn" onClick={() => onDelete(_id)}>Delete</button>
        </div>
      </div>
  );
}

export default TransactionCard;
