import React, { useState, useEffect } from 'react';

const tabs = [
  { key: 'gold', label: 'Gold' },
  { key: 'fd', label: 'Fixed Deposit' },
  { key: 'frb', label: 'Fixed Rate Bond' },
  { key: 'mf', label: 'Mutual Fund' }
];

const monthsMap = {
  '01': 'Jan', '02': 'Feb', '03': 'Mar', '04': 'Apr',
  '05': 'May', '06': 'Jun', '07': 'Jul', '08': 'Aug',
  '09': 'Sep', '10': 'Oct', '11': 'Nov', '12': 'Dec'
};

function getEmptyYearMap() {
  const map = {};
  for (let i = 1; i <= 12; i++) {
    const month = i.toString().padStart(2, '0');
    map[month] = [];
  }
  return map;
}

const Showinvest = ({ dataGold, dataFixedDeposit, dataFixedRateBond, dataMutualFund }) => {
  const [activeTab, setActiveTab] = useState('gold');
  const [selector, setSelector] = useState([]);
  const [year, setYear] = useState('');
  const [views, setViews] = useState({});

  const datasets = {
    gold: dataGold,
    fd: dataFixedDeposit,
    frb: dataFixedRateBond,
    mf: dataMutualFund
  };

  const preprocess = (type, dataset) => {
    const yearMap = {};
    dataset.forEach(entry => {
      const year = entry.date.slice(0, 4);
      const month = entry.date.slice(5, 7);
      if (!yearMap[year]) yearMap[year] = getEmptyYearMap();
      yearMap[year][month].push(entry);
    });
    return yearMap;
  };

  useEffect(() => {
    const processed = {};
    const yearsSet = new Set();
    Object.entries(datasets).forEach(([key, value]) => {
      processed[key] = preprocess(key, value);
      Object.keys(processed[key]).forEach(y => yearsSet.add(y));
    });
    const yearArr = [...yearsSet].sort((a, b) => b - a);
    setViews(processed);
    setSelector(yearArr);
    if (!yearArr.includes(year)) setYear(yearArr[0]);
  }, [dataGold, dataFixedDeposit, dataFixedRateBond, dataMutualFund]);

  const renderTableRows = (type) => {
    if (!views[type] || !views[type][year]) return null;
    return Object.entries(views[type][year]).map(([month, entries], i) => {
      const summary = entries.reduce((acc, e) => {
        if (type === 'gold') {
          acc.amount += e.purchaseAmount;
          acc.qty += e.qtyPurchased;
        } else if (type === 'fd') {
          acc.amount += e.deposit;
          acc.interest += e.interestRate;
        } else if (type === 'frb') {
          acc.amount += e.deposit;
          acc.interest += e.interestAtStart;
        } else if (type === 'mf') {
          acc.amount += e.purchaseAmount;
          acc.units += e.unitsBought;
        }
        return acc;
      }, { amount: 0, interest: 0, qty: 0, units: 0 });

      return (
        <tr key={i}>
          <td>{monthsMap[month]}</td>
          <td>{summary.amount}</td>
          {type === 'gold' && <td>{summary.qty}</td>}
          {type === 'fd' && <td>{summary.interest}</td>}
          {type === 'frb' && <td>{summary.interest}</td>}
          {type === 'mf' && <td>{summary.units}</td>}
        </tr>
      );
    });
  };

  return (
    <div>
      <h4>Investment Overview</h4>
      <div style={{ marginBottom: '1rem' }}>
        {tabs.map(tab => (
          <button
            key={tab.key}
            onClick={() => setActiveTab(tab.key)}
            style={{
              marginRight: '10px',
              backgroundColor: activeTab === tab.key ? '#333' : '#eee',
              color: activeTab === tab.key ? '#fff' : '#000',
              padding: '5px 10px',
              border: 'none',
              borderRadius: '4px'
            }}
          >
            {tab.label}
          </button>
        ))}
      </div>

      <select value={year} onChange={(e) => setYear(e.target.value)}>
        {selector.map((yr, i) => (
          <option key={i} value={yr}>{yr}</option>
        ))}
      </select>

      <table border="1" cellPadding="8" style={{ marginTop: '1rem', width: '100%' }}>
        <thead>
          <tr>
            <th>Month</th>
            <th>Amount</th>
            {(activeTab === 'gold') && <th>Qty</th>}
            {(activeTab === 'fd') && <th>Interest %</th>}
            {(activeTab === 'frb') && <th>Interest %</th>}
            {(activeTab === 'mf') && <th>Units</th>}
          </tr>
        </thead>
        <tbody>
          {renderTableRows(activeTab)}
        </tbody>
      </table>
    </div>
  );
};

export default Showinvest;
