import React, { useEffect, useState } from 'react'
import TakeInput from '../entries/TakeEntry'


const newstpc = [
    'all',
    'sports',
    'currency',
    'gold'
]


function News() {

    const newscat = TakeInput('all')

    const [news, setNews] = useState([]);
    
    const runFetch = async () => {

        const data = await fetch('https://newsapi.org/v2/everything?q=Apple&from=2025-06-29&sortBy=popularity&apiKey=4cab290f2317457ca27697ed745166a5')
        const jdata = await data.json();
        if (jdata) setNews(jdata);
    }

    useEffect(() => {
        runFetch();
    }, [])


    return (
        <>
            <select {...newscat}>
                {
                    news.map((val, ind) => (
                        console.log(val, ind)
                    ))
                }
            </select>
        </>
    )
}
export default News
