import React from 'react'
import TakeInput from '../TakeEntry'
import DebitFrom from './DebitFrom'
import { useAuth } from '../../../context/AuthContext'
import { useTrans } from '../../../context/TransactionContext';

function FixedInterestRateBond() {
      const { trans, setTrans } = useTrans();
    
  const { auth } = useAuth()
  const period = TakeInput(0)
  const deposit = TakeInput(0)
  const interestAtStart = TakeInput(0)
  const date = TakeInput(new Date().toISOString().substring(0, 16))
  const cmt = TakeInput('')
  const debit = TakeInput('SBI')

  const handleEntry = async () => {
    const body = {
      userId: auth.userId,
      period: parseFloat(period.val),
      deposit: parseFloat(deposit.val),
      interestAtStart: parseFloat(interestAtStart.val),
      date: new Date(date.val),
      cmt: cmt.val,
      debit: debit.val
    }


    try {
      const res = await fetch('http://localhost:4500/api/invest/fixedinterestratebond/fibentry', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(body)
      })

      const data = await res.json()
      console.log('Saved:', data)
      alert('Entry saved successfully!')
              setTrans(trans+1);

    } catch (err) {
      console.error('Error saving entry:', err)
      alert('Error saving entry.')
    }
  }

  return (
    <>
      <input placeholder='Enter Bond Period' type='number' {...period.bind} />
      <input placeholder='Enter Deposit Amount' type='number' {...deposit.bind} />
      <input placeholder='Enter Initial Interest Rate' type='number' {...interestAtStart.bind} />
      <input placeholder='Select Date' type='datetime-local' {...date.bind} />
      <input placeholder='Enter Comment' type='text' {...cmt.bind} />
      <select {...debit.bind}>
        {
          DebitFrom.map((val, key) => (
            <option key={key} value={val}>{val}</option>
          ))
        }
      </select>
      <button onClick={handleEntry}>Entry</button>
    </>
  )
}

export default FixedInterestRateBond
