// TransactionCard.js
import React from 'react'

function TransactionCard({ data, onDelete, onEdit }) {
  const { type, amt, desc, cmt, _id, date } = data;

  return (
    <td colSpan="4">
      <div style={styles.card}>
        <div style={styles.row}>
          <strong>Type:</strong> {type}
        </div>
        <div style={styles.row}>
          <strong>Amount:</strong> ₹{amt}
        </div>
        <div style={styles.row}>
          <strong>Description:</strong> {desc}
        </div>
        <div style={styles.row}>
          <strong>Comment:</strong> {cmt || "—"}
        </div>
        <div style={styles.row}>
          <strong>Date:</strong> {new Date(date).toLocaleString()}
        </div>

        <div style={styles.actions}>
          <button style={styles.editBtn} onClick={() => onEdit(_id)}>Edit</button>
          <button style={styles.deleteBtn} onClick={() => onDelete(_id)}>Delete</button>
        </div>
      </div>
    </td>
  );
}

const styles = {
  card: {
    padding: "1rem",
    margin: "0.5rem 0",
    backgroundColor: "#f8f9fa",
    borderRadius: "8px",
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
    textAlign: "left",
    display: "flex",
    flexDirection: "column",
    gap: "0.4rem"
  },
  row: {
    fontSize: "0.95rem"
  },
  actions: {
    marginTop: "0.5rem",
    display: "flex",
    justifyContent: "flex-end",
    gap: "0.5rem"
  },
  deleteBtn: {
    padding: "0.3rem 0.8rem",
    backgroundColor: "#dc3545",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer"
  },
  editBtn: {
    padding: "0.3rem 0.8rem",
    backgroundColor: "#007bff",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer"
  }
}

export default TransactionCard;
