import React, { useEffect, useState } from 'react'
import { useTrans } from '../../../../context/TransactionContext';




function MutualFundInsight({data}) {
  
      const [selector, setSelector] = useState({ Data: [] })
      const [curr, setCurr] = useState();
      const { trans } = useTrans()
      const [ choiceToSee, setChoiceToSee] = useState(true) // true means lumpsum, false means sip
  
      const findInv = () => {
  
          let totalInv = 0;
          let totalMaketSellPrice = selector.Qty * curr;
          totalInv = totalMaketSellPrice - selector.Amt;
  
          return totalInv;
      }
  
      const dataProcessing = () => {
          const dataMutualFund = data?.dataMutualFund;
          if (!Array.isArray(dataMutualFund)) return;
  
          let qtyTotal = 0;
          let amtTotal = 0;
          let invTotal = 0;
  
          dataMutualFund.forEach(element => {
              qtyTotal += Number(element.qtyPurchased || 0);
              amtTotal += Number(element.purchaseAmount || 0);
          });
  
          const totalMaketSellPrice = qtyTotal * (curr || 0);
          const totalInv = totalMaketSellPrice - amtTotal;
  
          setSelector({
              Qty: qtyTotal,
              Amt: amtTotal,
              Inv: totalInv,
              Data: dataMutualFund,
          });
      };
  
      useEffect(() => {
          dataProcessing();
      }, [])
  
      useEffect(() => {
          dataProcessing();
      }, [curr, trans, selector])
  
  
    return (

        <>
        {
            // <pre>{JSON.stringify(data, null, 2)}</pre>


        }
        <select onChange={(e)=>setChoiceToSee((prev)=>( e.target.value?prev:!prev ))} value={choiceToSee}>
            <option value={true}>Lumpsum</option>
            <option value={false}>SIP</option>
        </select>
          {
            choiceToSee
          }

        </>

  )
}

export default MutualFundInsight
