import React, { useEffect, useState } from 'react';
import TakeInput from '../entries/TakeEntry';

const categories = ['all', 'sports', 'currency', 'gold'];

function News() {
  const selectedCategory = TakeInput('all'); // You can handle category-based filtering later
  const [articles, setArticles] = useState([]);

  const runFetch = async () => {
    try {
      const res = await fetch(
        'https://newsapi.org/v2/everything?q=Apple&from=2025-06-29&sortBy=popularity&apiKey=4cab290f2317457ca27697ed745166a5'
      );
      const data = await res.json();
      if (data && data.articles) {
        setArticles(data.articles);
        console.log(articles)
      }
    } catch (err) {
      console.error("Failed to fetch news:", err);
    }
  };

  useEffect(() => {
    runFetch();
  }, []);

  return (
    <>
      <select {...selectedCategory}>
        {categories.map((topic, idx) => (
          <option key={idx} value={topic}>
            {topic}
          </option>
        ))}
      </select>

      <div>
        {articles.map((article, index) => (
          <div key={index} style={{ borderBottom: "1px solid #ccc", marginBottom: "1rem" }}>
            <h3>{article.title}</h3>
            <p>{article.description}</p>
            <a href={article.url} target="_blank" rel="noreferrer">Read more</a>
          </div>
        ))}
      </div>
    </>
  );
}

export default News;
