import React from 'react';
import TakeInput from '../TakeEntry';
import DebitFrom from './DebitFrom';
import { useAuth } from '../../../context/AuthContext';
import { useTrans } from '../../context/TransactionContext';



function FixedDeposit() {
      const { trans, setTrans } = useTrans();
    
    const { auth } = useAuth();
    const deposit = TakeInput(0);
    const interestRate = TakeInput(0);
    const date = TakeInput(new Date().toISOString().substring(0, 16));
    const cmt = TakeInput('');
    const debit = TakeInput('SBI');

    const handleEntry = async () => {
        if (!debit.val) {
            alert("Please select a debit account.");
            return;
        }

        try {
            const res = await fetch('http://localhost:4500/api/invest/fixeddeposit/fdentry', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    userId: auth.userId,
                    deposit: Number(deposit.val),
                    interestRate: Number(interestRate.val),
                    date: new Date(date.val),
                    cmt: cmt.val,
                    debit: debit.val
                })
            });

            const data = await res.json();
            if (res.ok) {
                alert("Fixed Deposit saved successfully!");
                        setTrans(trans+1);

                // Optionally reset fields here
            } else {
                console.error("Save failed:", data);
                alert("Save failed: " + (data.error || 'Unknown error'));
            }
        } catch (error) {
            console.error("Error:", error);
            alert("Something went wrong");
        }
    };

    return (
        <>
            <input placeholder='Enter Deposit Amount' type='number' {...deposit.bind} />
            <input placeholder='Enter Interest Rate' type='number' {...interestRate.bind} />
            <input placeholder='Enter Date' type='date' {...date.bind} />
            <input placeholder='Enter Comment' type='text' {...cmt.bind} />
            <select {...debit.bind}>
                <option value="">-- Select Debit From --</option>
                {
                    DebitFrom.map((val, key) => (
                        <option key={key} value={val}>{val}</option>
                    ))
                }
            </select>
            <button onClick={handleEntry}>Enter</button>
        </>
    );
}

export default FixedDeposit;
