import React from 'react'
import TakeInput from '../TakeEntry'
import DebitFrom from './DebitFrom'  // assumed to be an array like ['SBI', 'AXIS', ...]
import { useAuth } from '../../../context/AuthContext'
import { useTrans } from '../../../context/TransactionContext';



function Gold() {
      const { trans, setTrans } = useTrans();
    
  const { auth } = useAuth()
  const purchaseAmount = TakeInput(0)
  const marketSellAmount = TakeInput(0)
  const qtyPurchased = TakeInput(0)
  const date = TakeInput(new Date().toISOString().substring(0, 16))
  const cmt = TakeInput('')
  const debit = TakeInput('')

  const handleEntry = async () => {

    const data = {
      userId: auth.userId,
      purchaseAmount: parseFloat(purchaseAmount.val),
      marketSellAmount: parseFloat(marketSellAmount.val),
      qtyPurchased: parseFloat(qtyPurchased.val),
      date: new Date(date.val),
      comment: cmt.val,
      debitFrom: debit.val
    }

    console.log(data)

    try {
      const res = await fetch('http://localhost:4500/api/invest/gold/Entry', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
      })

      if (!res.ok) {
        const err = await res.json()
        throw new Error(err.error || 'Error saving gold entry')
      }
        setTrans(trans+1);

      alert('Gold entry saved successfully!')
    } catch (err) {
      alert('Failed to save: ' + err.message)
      console.error(err)
    }
  }

  return (
    <>
      <input placeholder='Enter Purchased Amount' type='number' {...purchaseAmount.bind} />
      <input placeholder='Enter Market Sell Amount' type='number' {...marketSellAmount.bind} />
      <input placeholder='Enter QTY Purchased' type='number' {...qtyPurchased.bind} />
      <input placeholder='Select Date' type='date' {...date.bind} />
      <input placeholder='Enter Comments' type='text' {...cmt.bind} />

      <select {...debit.bind} >
        <option value="">-- Select Debit From --</option>
        {DebitFrom.map((val, key) => (
          <option key={key} value={val}>{val}</option>
        ))}
      </select>

      <button onClick={handleEntry}>Enter</button>
    </>
  )
}

export default Gold
