import React, { useEffect, useState } from 'react'
import { useTrans } from '../../../../context/TransactionContext';




function MutualFundInsight({ data }) {

    const [selector, setSelector] = useState({ LumpsumData: [], SIPData: [] })
    const { trans } = useTrans()
    const [choiceToSee, setChoiceToSee] = useState(true) // true means lumpsum, false means sip


    const dataProcessing = () => {
        const dataMutualFund = data?.dataMutualFund || [];
        let lumpsumdata = []
        let sipdata = []

        dataMutualFund.forEach(item => {
           
             if( item.planType === 'LUMPSUM' ){
                   lumpsumdata.push(item)
             }else{
                   sipdata.push(item)
             }

        });

        setSelector({ lumpsumdata, sipdata })

      
    };

    useEffect(() => {
        dataProcessing();
    }, [])

    useEffect(() => {
        dataProcessing();
    }, [ trans, selector])


    return (

        <>
            {
                // <pre>{JSON.stringify(data, null, 2)}</pre>
            }
            <br/>
            <select
                onChange={(e) => setChoiceToSee(e.target.value === "true")}
                value={choiceToSee.toString()}
            >            <option value={true}>Lumpsum</option>
                <option value={false}>SIP</option>
            </select>

            {
                choiceToSee? showLump(selector.LumpsumData) : showSip(selector.SIPData)
            }
            



        </>

    )
}

const showLump=(data)=>{

    return (
        <>
        {
            data.map((val, key)=>(
                <div key={key}>
                    <label>Plan Name</label> <strong>{val.comment}</strong>
                    <label>Purchase Amount</label> <strong>{val.purchaseAmount}</strong>
                    <label>Units Bought</label><strong>{val.unitsBought}</strong>
                    <label>Unit Costed</label><strong>{val.unitCost}</strong>
                    <label>Date Purchased</label><strong>{val.date.slice(0,10)}</strong>
                    <label>Bought From</label><strong></strong>
                </div>
                
            ))
        }
        </>
    )
}

