// TransactionList.js
import React, { useEffect, useState } from 'react'
import { useTrans } from '../../context/TransactionContext'
import TransactionCard from './TransactionCard'
import { useAuth } from '../../context/AuthContext'
import './TransactionList.css'

function TransactionList() {
  const [trasact, setTrasact] = useState([])
  const { auth } = useAuth()
  const { trans } = useTrans()
  
  const runFetch = async () => {
    const res = await fetch(`http://localhost:4500/api/trasact/getAllTransactions/${auth.userId}`)
    const jdata = await res.json()
    if (jdata) setTrasact(jdata)
  }

  const handleDelete = async (id) => {
    const res = await fetch(`http://localhost:4500/api/trasact/deleteTransaction/${id}`, {
      method: 'DELETE',
    })
    if (res.ok) {
      setTrasact(prev => prev.filter(t => t._id !== id))
            const { trans, setTrans } = useTrans();
      
    }
  }

  const handleEdit = async (id) => {
    const entry = trasact.find(t => t._id === id)
    const newAmt = prompt("Enter new amount:", entry.amt)
    if (newAmt !== null) {
      const res = await fetch(`http://localhost:4500/api/trasact/updateTransaction/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ amt: Number(newAmt) }),
      })
      if (res.ok) runFetch()
    }
  }

  useEffect(() => {
    runFetch()
  }, [trans])

 return (
  <div style={{ padding: '1rem', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
    <h3>Transaction List</h3>
    {trasact.map((val, ind) => (
      <TransactionCard
        key={ind}
        data={val}
        onDelete={handleDelete}
        onEdit={handleEdit}
      />
    ))}
  </div>
);

}

export default TransactionList
