import React, { useEffect, useState } from 'react'

const months = {
    '01': 'Jan',
    '02': 'Feb',
    '03': 'Mar',
    '04': 'Apr',
    '05': 'May',
    '06': 'Jun',
    '07': 'Jul',
    '08': 'Aug',
    '09': 'Sep',
    '10': 'Oct',
    '11': 'Nov',
    '12': 'Dec',
};


const quaterly = {
    1: ['01', 'Jan'],
    2: ['04', 'Apr'],
    3: ['07', 'July'],
    4: ['10', 'Oct']
}




function Showsave({ data }) {

    const [duration, setDuration] = useState('')
    const [ categories, setCategories] = useState([])

    const durationSelector = () => {

        return (
            <>
                <select value={duration} onChange={(e) => setDuration(e.target.value)}>
                    <option value='monthly'>Monthly</option>
                    <option value='quaterly'>Quantery</option>
                    <option value='yearly'>Yearly</option>
                </select>
            </>
        )
    }

    const handleColSpan = () => {

    }

    const getUniqueCategories = () => {
    const cats = [];
    for (let i = 0; i < data.length; i++) {
        const cat = data[i].category;
        if (!cats.includes(cat)) {
            cats.push(cat);
        }
    }
    setCategories(cats);
};


    useEffect(() => {
      getUniqueCategories()
    }, [])
    useEffect(() => {
      
      console.log('this is cats',categories)
    }, [])

    return (
        <>
            <h4>Saving Insights</h4>
            <table>
                <tr>
                    <th>Account</th>
                    <th colSpan={handleColSpan}>{durationSelector()}</th>
                </tr>
                {
                 categories.map((val, key)=>(
                    <>
                    {}
                    </>
                 ))
                }
            </table>
        </>

    )
}

export default Showsave
