import React, { useEffect } from 'react'
import '../dashboard/styleDash.css'
import TakeInput from '../entries/TakeEntry'
import Expense from './Expense'
import Save from './Save'
import Invest from './Invest'
import { useAuth } from '../../context/AuthContext'
import { useTrans } from '../../context/TransactionContext';



function DashCard() {
    const save = TakeInput(true)
    const invest = TakeInput(false)
    const exp = TakeInput(false)
    const i = TakeInput(0)
    const saveData = TakeInput(null)
    const investData = TakeInput(null)
    const expData = TakeInput(null)
    const { auth } = useAuth();
    const { trans, setTrans} = useTrans();


    const handleChangeViewDash = () => {
        const next = (parseInt(i.val) + 1) % 3
        i.setVal(next)

        if (next === 0) {
            save.setVal(true)
            invest.setVal(false)
            exp.setVal(false)
        } else if (next === 1) {
            save.setVal(false)
            invest.setVal(true)
            exp.setVal(false)
        } else {
            save.setVal(false)
            invest.setVal(false)
            exp.setVal(true)
        }
    
    }

    

    const runFetch=async()=>{
        const data = await fetch(`http://localhost:4500/api/dashdata/getAll${auth.userId}`,{
            method: 'GET',
            headers:{
                'Content-Type':'application/json'
            }
        })
        const jdata = await data.json()

    }






    useEffect(() => {
      runFetch();
    }, [trans])
    



    return (
        <div className='dash'>
            <div className='viewDash'>
                {save.val && <Save />}
                {invest.val && <Invest />}
                {exp.val && <Expense />}
            </div>
            <div className='changeViewDash'>
                <p>{trans}</p>
                <button onClick={handleChangeViewDash}>NEXT</button>
            </div>
        </div>
    )
}

export default DashCard
