import React, { useEffect, useState } from 'react';
import TakeInput from '../entries/TakeEntry';
import '../news/News.css'



const categories = ['Money','Gold', 'Economics', 'currency', 'Gas', 'RealEstate', 'MutualFunds', 'WorldWar', 'Investment'];
const sortChoices = ['Popularity', 'Relevancy', 'PublishedAt']

function News() {
  const selectedCategory = TakeInput('all'); // You can handle category-based filtering later
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [ apiQ, setApiQ] = useState(['Money','Popularity']);



  const runFetch = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log("Fetching news...");
      
      // Use a CORS proxy to bypass browser restrictions
      const apiUrl = `https://newsapi.org/v2/everything?q=${apiQ[0]}&sortBy=${apiQ[1]}&apiKey=4cab290f2317457ca27697ed745166a5`
      
      const res = await fetch(apiUrl, {
        headers: {
          'Origin': window.location.origin
        }
      });
      
      if (!res.ok) {
        throw new Error(`HTTP error! status: ${res.status}`);
      }
      
      const data = await res.json();
      console.log("Raw API response:", data);
      
      if (data && data.articles && data.articles.length > 0) {
        setArticles(data.articles);
        console.log("Updated articles:", data.articles.length, "articles found");
      } else {
        console.log("No articles found in response");
        setArticles([]);
      }
    } catch (err) {
      console.error("Failed to fetch news:", err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    runFetch();
    // Remove the setTimeout as it's not needed and can cause issues
  }, []);

  if (loading) {
    return <div>Loading news...</div>;
  }

  if (error) {
    return <div>Error loading news: {error}</div>;
  }

  return (
    <>
      <select {...selectedCategory}>
        {categories.map((topic, idx) => (
          <option key={idx} value={topic}>
            {topic}
          </option>
        ))}
      </select>

      <div className='newsComponent'>
  {articles.length === 0 ? (
    <div>No news articles available</div>
  ) : (
    articles.map((article, index) => (
      <div key={index} className="news-article">
        <h3>{article.title}</h3>
        <p>{article.description || "No description available."}</p>
        <a href={article.url} target="_blank" rel="noreferrer">Read more</a>
      </div>
    ))
  )}
</div>

    </>
  );
}

export default News;
