    },
    Shelter,
    Gas,
    Bike,
    Stationary,
    Smoke,
    Shopping,
    Washing,
    Wine,
    Medicine,
    Jio: {
        Cell, Wifi
    },
    Misc
}

function ExpenseEntry() {
    const category = TakeInput('Food')
    const amt = TakeInput(0)
    const date = TakeInput(new Date().toISOString().substring(0,16))
    const cmt = TakeInput('')
    const debit = TakeInput('SBI')


    return (
        <>
        <select { ...category} >
            {
                Object.keys(expCat)
            }
        </select>
        <input type='number' placeholder='Enter Expense Amount' />
        <input type='date' placeholder='Enter Date' />
        <input type='text' placeholder='Enter Comment' />
        <button onClick={handleEntry}>Enter</button>
        </>
    )
}

export default ExpenseEntry
