import React from 'react';
import { PieChart, Pie, Cell, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { useModal } from '../../context/ModalContext';
import InsightCard from '../insight/InsightCard';
import { useAuth } from '../../context/AuthContext';

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];

function Save({ data }) {
  const { auth } = useAuth();
  const { showModal } = useModal();

  const showInsight = () => {
    showModal(<InsightCard value={'save'} />);
  };

  if (!data) return null;

  const chartData = [
    { name: 'SBI', value: data.SBI || 0 },
    { name: 'AXIS', value: data.AXIS || 0 },
    { name: 'IDBI', value: data.IDBI || 0 },
    { name: 'MISC', value: data.MISC || 0 },
  ].filter(item => item.value > 0); // filter out 0s

  return (
    <>
      <button onClick={showInsight}>Insight</button>

      <div style={{ display: 'flex', justifyContent: 'space-between', gap: '2rem', flexWrap: 'wrap' }}>

        {/* LEFT TABLE */}
        <div className='left' style={{ flex: '1' }}>
          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th style={{ borderBottom: '1px solid #ccc', textAlign: 'left', padding: '8px' }}>Bank</th>
                <th style={{ borderBottom: '1px solid #ccc', textAlign: 'right', padding: '8px' }}>Amount (â‚¹)</th>
              </tr>
            </thead>
            <tbody>
              {chartData.map((entry, index) => (
               <> <tr key={index}>
                  <td style={{ padding: '8px' }}>{entry.name}</td>
                  <td style={{ padding: '8px', textAlign: 'right' }}>{entry.value}</td>
                </tr>
                <tr>
                    <td>TOTAL</td>
                    <td>{}</td>
                </tr>
                </>
              ))}
            </tbody>
          </table>
        </div>

        {/* RIGHT PIE CHART */}
        <div className='right' style={{ flex: '1', minWidth: '300px', height: 300 }}>
          <ResponsiveContainer>
            <PieChart>
              <Pie
                data={chartData}
                dataKey="value"
                nameKey="name"
                cx="50%"
                cy="50%"
                outerRadius={100}
                fill="#8884d8"
                label
              >
                {chartData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
              <Legend />
            </PieChart>
          </ResponsiveContainer>
        </div>

      </div>
    </>
  );
}

export default Save;
