import React from 'react'
import Inputuser from './Inputuser'


function Login() {

    const user = Inputuser('');
    const password = Inputuser('');

    const handleSub=async(e)=>{
        e.preventDefault();
        const data = await fetch("",{
            method:'GET',
            body:JSON.stringify(user.user, password.user),
            headers:{
                "Content-Type":"application/json"
            }
        })
        const jdata = await data.json()

    }

  return (
    <>
    <form onSubmit={handleSub}>
    <input placeholder='Enter Your Username or Email' name='user' { ...user.bind}/>
    <input placeholder='Enter Your Password' name='password' { ...password.bind}/>
    <button type='submit'>Submit</button>
    </form>
    </>
  )
}

export default Login
