import React from 'react';
import TakeInput from '../TakeEntry';
import DebitFrom from './DebitFrom';
import { useAuth } from '../../../context/AuthContext';
import { useTrans } from '../../../context/TransactionContext';

import '../'

function FixedDeposit() {
  const { trans, setTrans } = useTrans();
  const { auth } = useAuth();

  const deposit = TakeInput(0);
  const interestRate = TakeInput(0);
  const date = TakeInput(new Date().toISOString().substring(0, 10)); // YYYY-MM-DD for type="date"
  const cmt = TakeInput('');
  const debit = TakeInput('');

  const handleEntry = async () => {
    if (!debit.val) {
      alert("Please select a debit account.");
      return;
    }

    try {
      const res = await fetch('http://localhost:4500/api/invest/fixeddeposit/fdentry', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          userId: auth.userId,
          deposit: Number(deposit.val),
          interestRate: Number(interestRate.val),
          date: new Date(date.val),
          cmt: cmt.val,
          debit: debit.val,
        }),
      });

      const data = await res.json();
      if (res.ok) {
        alert("Fixed Deposit saved successfully!");
        setTrans(trans + 1);
        // optionally reset inputs here if needed
      } else {
        alert("Save failed: " + (data.error || "Unknown error"));
      }
    } catch (error) {
      alert("Something went wrong");
      console.error(error);
    }
  };

  return (
    <div className="fd-container">
      <input
        type="number"
        placeholder="Enter Deposit Amount"
        {...deposit.bind}
        className="fd-input"
        min="0"
      />
      <input
        type="number"
        placeholder="Enter Interest Rate (%)"
        {...interestRate.bind}
        className="fd-input"
        min="0"
        step="0.01"
      />
      <input
        type="date"
        placeholder="Select Date"
        {...date.bind}
        className="fd-input"
      />
      <input
        type="text"
        placeholder="Enter Comment"
        {...cmt.bind}
        className="fd-input"
      />
      <select {...debit.bind} className="fd-select">
        <option value="">-- Select Debit From --</option>
        {DebitFrom.map((val, key) => (
          <option key={key} value={val}>
            {val}
          </option>
        ))}
      </select>
      <button onClick={handleEntry} className="fd-button">
        Enter
      </button>
    </div>
  );
}

export default FixedDeposit;
