import React from 'react';
import TakeInput from './TakeEntry';
import { useAuth } from '../../context/AuthContext'

const catSave = ['SBI', 'AXIS', 'IDBI'];

function SaveEntry() {
  const { auth } = useAuth(); // get userId and token from context
  const category = TakeInput('SBI');
  const deposit = TakeInput(0);
  const date = TakeInput(new Date().toISOString().substring(0, 10)); // only YYYY-MM-DD
  const cmt = TakeInput('');

  const handleEntry = async () => {
    if (!auth?.userId || !auth?.token) {
      alert("You're not logged in.");
      return;
    }
console.log('Saving:', {
  userId: auth.userId,
  category: category.user,
  deposit: deposit.user,
  date: date.user,
  comment: cmt.user,
});    try {
      const res = await fetch('http://localhost:4500/api/transaction/saveThis', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${auth.token}`, // send JWT
        },
        body: JSON.stringify({
          userId: auth.userId,
          category: category.val,
          deposit: parseFloat(deposit.val),
          date: new Date(date.val),
          comment: cmt.val,
        }),
      });

      const data = await res.json();
      if (!res.ok) {
        alert(data.message || 'Failed to save entry');
        return;
      }

      alert('Entry saved successfully!');
    } catch (err) {
      console.error('Save error:', err);
      alert('Something went wrong');
    }
  };



  return (
    <>
      <select name="category" {...category.bind}>
        {catSave.map((val, ind) => (
          <option value={val} key={ind}>
            {val}
          </option>
        ))}
      </select>
      <input
        type="number"
        {...deposit.bind}
        placeholder="Enter Amount To Be Deposited"
      />
      <input type="date" {...date.bind} placeholder="Select Date" />
      <input type="text" {...cmt.bind} placeholder="Enter Comments" />
      <button onClick={handleEntry}>Enter</button>
    </>
  );
}

export default SaveEntry;
