import React, { useEffect, useState } from 'react';
import { useTrans } from '../../../../context/TransactionContext';
import './MutualFund.css';

function MutualFundInsight({ data }) {
  const [selector, setSelector] = useState({ LumpsumData: [], SIPData: [] });
  const { trans } = useTrans();
  const [choiceToSee, setChoiceToSee] = useState(true); // true = Lumpsum

  const dataProcessing = () => {
    const dataMutualFund = data?.dataMutualFund || [];
    const lumpsumdata = [];
    const sipdata = [];

    dataMutualFund.forEach(item => {
      if (item.planType === 'LUMPSUM') {
        lumpsumdata.push(item);
      } else {
        sipdata.push(item);
      }
    });

    setSelector({ LumpsumData: lumpsumdata, SIPData: sipdata });
  };

  useEffect(() => {
    dataProcessing();
  }, [trans, data]);

  return (
    <>
      <div className="mf-toggle">
        <select
          onChange={(e) => setChoiceToSee(e.target.value === 'true')}
          value={choiceToSee.toString()}
        >
          <option value={true}>Lumpsum</option>
          <option value={false}>SIP</option>
        </select>
      </div>

      {
        choiceToSee
          ? <LumpsumView data={selector.LumpsumData} />
          : <SipView data={selector.SIPData} />
      }
    </>
  );
}

function LumpsumView({ data }) {
  const [lumpsumVal, setLumpsumVal] = useState({});
  const [result, setResult] = useState({});

  const handleLog = (key) => {
    const currentVal = Number(lumpsumVal[key]);
    const profit = currentVal - data[key].purchaseAmount;
    setResult(prev => ({ ...prev, [key]: profit }));
  };

  return (
    <div className="mf-grid">
      {data.map((val, key) => (
        <div key={key} className="mf-card">
          <p><strong>Plan:</strong> {val.comment || 'Unnamed'}</p>
          <p><strong>Amount:</strong> ₹{val.purchaseAmount}</p>
          <p><strong>Units:</strong> {val.unitsBought}</p>
          <p><strong>Unit Cost:</strong> ₹{val.unitCost}</p>
          <p><strong>Date:</strong> {val.date.slice(0, 10)}</p>
          <p><strong>From:</strong> {val.debitFrom}</p>
          <input
            placeholder="Current Value"
            value={lumpsumVal[key] || ''}
            onChange={(e) => setLumpsumVal(prev => ({ ...prev, [key]: e.target.value }))}
          />
          <button onClick={() => handleLog(key)}>Log</button>
          <p><strong>P/L:</strong> {result[key] !== undefined ? `₹${result[key]}` : '-'}</p>
        </div>
      ))}
    </div>
  );
}

function SipView({ data }) {
  const [sipVal, setSipVal] = useState({});
  const [sipResult, setSipResult] = useState({});

  const handleLog = (key) => {
    const currentVal = Number(sipVal[key]);
    const profit = currentVal - data[key].purchaseAmount;
    setSipResult(prev => ({ ...prev, [key]: profit }));
  };

  return (
    <div className="mf-grid">
      {data.map((val, key) => (
        <div key={key} className="mf-card">
          <p><strong>SIP Plan:</strong> {val.comment || 'Unnamed SIP'}</p>
          <p><strong>Amount:</strong> ₹{val.purchaseAmount}</p>
          <p><strong>Units:</strong> {val.unitsBought}</p>
          <p><strong>Unit Cost:</strong> ₹{val.unitCost}</p>
          <p><strong>Date:</strong> {val.date.slice(0, 10)}</p>
          <p><strong>Source:</strong> {val.debitFrom}</p>
          <input
            placeholder="Current Value"
            value={sipVal[key] || ''}
            onChange={(e) => setSipVal(prev => ({ ...prev, [key]: e.target.value }))}
          />
          <button onClick={() => handleLog(key)}>Log</button>
          <p><strong>P/L:</strong> {sipResult[key] !== undefined ? `₹${sipResult[key]}` : '-'}</p>
        </div>
      ))}
    </div>
  );
}

export default MutualFundInsight;
