import React, { useState, useEffect } from 'react';
import './Showinvest.css';

const tabs = ['Gold', 'Fixed Deposit', 'Fixed Rate Bond', 'Mutual Fund'];

const extractors = {
  Gold: item => ({
    date: item.date,
    debitFrom: item.debitFrom,
    qty: item.qtyPurchased,
    purchaseAmount: item.purchaseAmount,
    marketSellAmount: item.marketSellAmount,
    comment: item.comment
  }),
  'Fixed Deposit': item => ({
    date: item.date,
    debit: item.debit,
    deposit: item.deposit,
    interestRate: item.interestRate,
    cmt: item.cmt
  }),
  'Fixed Rate Bond': item => ({
    date: item.date,
    debit: item.debit,
    deposit: item.deposit,
    interestAtStart: item.interestAtStart,
    period: item.period,
    cmt: item.cmt
  }),
  'Mutual Fund': item => ({
    date: item.date,
    debitFrom: item.debitFrom,
    planType: item.planType,
    unitsBought: item.unitsBought,
    unitCost: item.unitCost,
    purchaseAmount: item.purchaseAmount,
    comment: item.comment
  })
};

const headers = {
  Gold: ['date', 'debitFrom', 'qty', 'purchaseAmount', 'marketSellAmount', 'comment'],
  'Fixed Deposit': ['date', 'debit', 'deposit', 'interestRate', 'cmt'],
  'Fixed Rate Bond': ['date', 'debit', 'deposit', 'interestAtStart', 'period', 'cmt'],
  'Mutual Fund': ['date', 'debitFrom', 'planType', 'unitsBought', 'unitCost', 'purchaseAmount', 'comment']
};

function Showinvest({ data }) {
  const [activeTab, setActiveTab] = useState('Gold');
  const [selector, setSelector] = useState([]);
  const [view, setView] = useState('');
  const [giveView, setGiveView] = useState({});
  const [sortBy, setSortBy] = useState('');

  const getTabData = () => {
    switch (activeTab) {
      case 'Gold':
        return data?.dataGold || [];
      case 'Fixed Deposit':
        return data?.dataFixedDeposit || [];
      case 'Fixed Rate Bond':
        return data?.dataFixedRateBond || [];
      case 'Mutual Fund':
        return data?.dataMutualFund || [];
      default:
        return [];
    }
  };

  const preprocess = (arr, extractor) => {
    const yearMap = {};
    arr.forEach(item => {
      const year = item.date?.slice(0, 4);
      const month = item.date?.slice(5, 7);
      if (!yearMap[year]) yearMap[year] = {};
      if (!yearMap[year][month]) yearMap[year][month] = [];
      yearMap[year][month].push(extractor(item));
    });
    return yearMap;
  };

  useEffect(() => {
    const tabData = getTabData();
    const extracted = preprocess(tabData, extractors[activeTab]);
    const sortedYears = Object.entries(extracted).sort((a, b) => b[0] - a[0]);
    setSelector(sortedYears);
    if (sortedYears.length > 0) {
      setView(sortedYears[0][0]);
      setGiveView(sortedYears[0][1]);
    } else {
      setView('');
      setGiveView({});
    }
  }, [activeTab, data]);

  useEffect(() => {
    const yearMatch = selector.find(([yr]) => yr === view);
    if (yearMatch) setGiveView(yearMatch[1]);
  }, [view, selector]);

  const currentHeaders = headers[activeTab];
  const flattened = Object.values(giveView).flat();

  const sortedEntries = sortBy
    ? [...flattened].sort((a, b) => {
        if (typeof a[sortBy] === 'number') return b[sortBy] - a[sortBy];
        return String(b[sortBy] || '').localeCompare(String(a[sortBy] || ''));
      })
    : flattened;

  return (
    <div className="investment-tabs">
      <div className="tab-buttons">
        {tabs.map(tab => (
          <button
            key={tab}
            className={tab === activeTab ? 'active' : ''}
            onClick={() => setActiveTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      <div className="selectors">
        <label>Year:</label>
        <select value={view} onChange={e => setView(e.target.value)}>
          {selector.map(([yr]) => (
            <option key={yr} value={yr}>{yr}</option>
          ))}
        </select>

        <label>Sort By:</label>
        <select value={sortBy} onChange={e => setSortBy(e.target.value)}>
          <option value="">None</option>
          {currentHeaders.map(head => (
            <option key={head} value={head}>{head}</option>
          ))}
        </select>
      </div>

      <table className="investment-table">
        <thead>
          <tr>
            {currentHeaders.map(h => (
              <th key={h}>{h}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {sortedEntries.map((entry, idx) => (
            <tr key={idx}>
              {currentHeaders.map(h => (
                <td key={h}>{entry[h] ?? '-'}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Showinvest;
