import React, { useEffect, useState } from 'react';
import '../details/Showsave.css';

const months = {
  '1': 'Jan',
  '2': 'Feb',
  '3': 'Mar',
  '4': 'Apr',
  '5': 'May',
  '6': 'Jun',
  '7': 'Jul',
  '8': 'Aug',
  '9': 'Sep',
  '10': 'Oct',
  '11': 'Nov',
  '12': 'Dec',
};

const Showsave = ({ data }) => {
  const [duration, setDuration] = useState('monthly');
  const [categories, setCategories] = useState([]);
  const [records, setRecords] = useState([]);
  const [tableData, setTableData] = useState({}); // { 'category': { '2025-05': amount, ... }, ... }

  const durationSelector = () => (
    <select value={duration} onChange={(e) => setDuration(e.target.value)}>
      <option value="monthly">Monthly</option>
      <option value="quaterly">Quarterly</option>
      <option value="yearly">Yearly</option>
    </select>
  );

  const getUniqueCategories = () => {
    const unique = [...new Set(data.map((d) => d.category))];
    setCategories(unique);
  };

  const generateRecords = () => {
    const recs = [];
    let month = new Date().getMonth() + 1;
    let year = new Date().getFullYear();

    for (let i = 0; i < 12; i++) {
      const mStr = month.toString().padStart(2, '0');
      const dateKey = `${year}-${mStr}`;
      recs.push(dateKey);

      switch (duration) {
        case 'monthly':
          month -= 1;
          break;
        case 'quaterly':
          month -= 3;
          break;
        case 'yearly':
          month -= 12;
          break;
        default:
          break;
      }

      while (month <= 0) {
        month += 12;
        year -= 1;
      }
    }

    return recs.reverse(); // oldest to newest
  };

  const buildTableData = (recs, cats) => {
    const tempData = {};
    for (const cat of cats) {
      tempData[cat] = {};
      for (const rec of recs) {
        tempData[cat][rec] = 0;
      }
    }

    data.forEach((item) => {
      const [y, m] = item.date.split('-');
      const key = `${y}-${m}`;
      if (recs.includes(key)) {
        if (tempData[item.category]) {
          tempData[item.category][key] += Number(item.deposit || 0);
        }
      }
    });

    return tempData;
  };

  useEffect(() => {
    if (data && data.length > 0) {
      const uniqueCats = [...new Set(data.map((d) => d.category))];
      setCategories(uniqueCats);

      const recs = generateRecords();
      setRecords(recs);

      const table = buildTableData(recs, uniqueCats);
      setTableData(table);
    }
  }, [data, duration]);

  const COLUMN_WIDTH = 100;
  const VISIBLE_COLUMNS = 4;

  return (
    <>
      <h4>Saving Insights</h4>
      <div
        style={{
          overflowX: 'auto',
          width: `${(VISIBLE_COLUMNS + 1) * COLUMN_WIDTH}px`,
          border: '1px solid #ccc',
        }}
      >
        <table
          style={{
            width: `${(records.length + 1) * COLUMN_WIDTH}px`,
            borderCollapse: 'collapse',
          }}
        >
          <thead>
            <tr>
              <th style={{ width: `${COLUMN_WIDTH}px` }}>Account</th>
              <th
                colSpan={records.length}
                style={{ width: `${records.length * COLUMN_WIDTH}px` }}
              >
                {durationSelector()}
              </th>
            </tr>
            <tr>
              <td></td>
              {records.map((val, key) => (
                <td
                  key={key}
                  style={{
                    width: `${COLUMN_WIDTH}px`,
                    border: '1px solid #ccc',
                    textAlign: 'center',
                  }}
                >
                  {val}
                </td>
              ))}
            </tr>
          </thead>
          <tbody>
            {categories.map((cat, key) => (
              <tr key={key}>
                <td
                  style={{
                    width: `${COLUMN_WIDTH}px`,
                    border: '1px solid #ccc',
                  }}
                >
                  {cat}
                </td>
                {records.map((rec, i) => (
                  <td
                    key={i}
                    style={{
                      width: `${COLUMN_WIDTH}px`,
                      border: '1px solid #ccc',
                      textAlign: 'center',
                    }}
                  >
                    ₹{tableData?.[cat]?.[rec] || 0}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default Showsave;
