import React, { useState, useEffect } from 'react';
import { useTrans } from '../../../context/TransactionContext';

const baseBanks = { SBI: 0, AXIS: 0, IDBI: 0, MISC: 0 };

const monthsMap = {
  '01': 'Jan', '02': 'Feb', '03': 'Mar', '04': 'Apr',
  '05': 'May', '06': 'Jun', '07': 'Jul', '08': 'Aug',
  '09': 'Sep', '10': 'Oct', '11': 'Nov', '12': 'Dec'
};

function getFreshYearMap() {
  const monthObj = {};
  for (let i = 1; i <= 12; i++) {
    const num = i.toString().padStart(2, '0');
    const name = new Date(2000, i - 1).toLocaleString('en-US', { month: 'short' });
    monthObj[num] = [name, { ...baseBanks }];
  }
  return monthObj;
}


function Showsave({ data }) {
  const [duration, setDuration] = useState('monthly');
  const [ selector, setSelector] = useState([]);
  const { trans } = useTrans();
  const yearMap = {}


  const dataProcessing = () => {
    
   data.forEach(entry => {
    const year = entry.createdAt.slice(0, 4);
    const month = entry.createdAt.slice(5, 7);
    const bank = entry.category;
    const amount = Number(entry.deposit || 0);
    if(!yearMap[year]){
       yearMap = { ... [year, ...baseBanks]}
    }
    


   });

  };

  useEffect(() => {
    if (data && data.length > 0) {
      dataProcessing();
    }
  }, [data, trans]);

  useEffect(() => {
    console.log('ðŸ“Š Final yearSelector:', yearSelector);
  }, [yearSelector]);

  return (
    <div>
      <h4>Saving Insight Summary</h4>
      <pre>{JSON.stringify(yearSelector, null, 2)}</pre>
    </div>
  );
}

export default Showsave;
