import React, { useState } from 'react';
import Login from './Login';
import Register from './Register';

function Form() {
  const [activeForm, setActiveForm] = useState(0); // 0 = Login, 1 = Register

  const renderForm = () => {
    switch (activeForm) {
      case 0:
        return <Login />;
      case 1:
        return <Register />;
      default:
        return null;
    }
  };

  return (
    <>
      <div className="formDiv">
        <div className="formChoiceButton">
          <label>
            <button onClick={() => setActiveForm(0)}>Login</button>
            <button onClick={() => setActiveForm(1)}>Register</button>
          </label>
        </div>
        <div className="formContainer">
          {renderForm()}
        </div>
      </div>
    </>
  );
}

export default Form;
