import { useState } from "react"


function Inputuser(val){

    const [ user, setUser] = useState(val);

    const handleChange=(e)=>{
        const { name, value} = e.target
        setUser((prev)=>({
            ...prev,
            [name]:value
    }))
    }

    const reset=()=>{
        setUser(val);
    }

    return {
      user,
      handleChange,
      reset,
      bind:{
        value:user,
        onchange:handleChange
      }
    }
}

export default Inputuser;
