import React, { useEffect, useState } from 'react'
import { useTrans } from '../../context/TransactionContext'
import TransactionCard from './TransactionCard';
import { useAuth } from '../../context/AuthContext';

function TransactionList() {

    const [trasact, setTrasact] = useState(null);
    const { auth } = useAuth();
    const { trans } = useTrans();
    
    const runFetch=async()=>{
        const data = await fetch(`http://localhost:4500/api/trasact/getAllTransactions/${auth.userId}`,{
            headers: {
                'Content-Type':'application/json'
            },
            method: 'GET'
        })
        const jdata = await data.json()
        if(jdata){
            setTrasact(jdata)
            console.log("I am giving this ", jdata)
        }
    }


    useEffect(() => {

        runFetch();

    }, [trans])
    


    return (
        <>
        {
            trasact.map((val, ind)=>{
                <tr key={ind}>
                    <TransactionCard data={val} />
                </tr>
            })
        }
         {/* <TransactionCard data={trasact} /> */}
        </>
    )
}

export default TransactionList
