  const dataProcessing=()=>{
     let dataGold = data['dataGold'];
     let qtyTotal = 0
     let amtTotal = 0
     let invTotal = 0
     dataGold.map((val, ind)=>{
      
        qtyTotal += val.qtyPurchased;
        amtTotal += val.purchaseAmount;
        
     })

    }


  return (
    <>
    <p>Net Qty: </p>
    <p>Net Amt: </p>
    <p>Net Profit/Loss: </p>
    <input placeholder='Enter Current Sell Pricce Of Gold' name='curr' value={curr} onChange={(e)=>setCurr(e.target.value)} />
    </>
  )
}

